INSERT INTO `ls_itemtabdata` (`id`, `itemtab_id`, `item_id`, `text`, `textmob`) VALUES
(5, 7, 15, '<p><strong>Ноутбук</strong> (англ.&nbsp;<em><span lang="en" xml:lang="en">notebook</span></em>&nbsp;&mdash; блокнот, портативный ПК)&nbsp;&mdash; переносной персональный компьютер, в корпусе которого объединены типичные компоненты ПК, включая дисплей, клавиатуру и устройство указания (обычно сенсорная панель, или тачпад), а также аккумуляторные батареи. Ноутбуки отличаются небольшими размерами и весом, время автономной работы ноутбуков изменяется в пределах от 2 до 15 часов.</p>\r\n<p><strong>Лэптоп</strong> (англ.&nbsp;<em><span lang="en" xml:lang="en">laptop</span></em>&nbsp;&mdash; lap = колени сидящего человека, top = верх)&nbsp;&mdash; более широкий термин, он применяется как к ноутбукам, так и нетбукам,смартбукам. Исторически (в начале 1990-х годов) лэптопами называли портативные (переносные) персональные компьютеры в форм-факторе "дипломат" (делового портфеля). При открытии верхнюю часть занимал экран (LCD, изначально монохромный), а в нижней располагалась клавиатура и иные устройства ввода (например, трэк-болл).</p>\r\n<p>К ноутбукам обычно относят лэптопы (часто употребляется неправильно&nbsp;&mdash; &laquo;лаптоп&raquo;), выполненные в раскладном форм-факторе. Ноутбук переносят в сложенном виде, это позволяет защитить экран, клавиатуру и тачпад при транспортировке. Также это связано с удобством транспортировки (чаще всего ноутбук транспортируется в портфеле, что позволяет не держать его в руках, а повесить на плечо, к примеру).</p>', ''),
(6, 7, 16, '', '');
